mapper.select:
		SELECT *
		FROM
		(SELECT V.*
        FROM V_BUS_INFO V
        JOIN TOWN T ON(T.T_NO = V.T_NO)



				AND ADDRESS_3 = #{address_3}
				AND ADDRESS_2 = #{address_2}
				AND ADDRESS_1 = #{address_1}


				AND ADDRESS_2 = #{address_2}
				AND ADDRESS_1 = #{address_1}


				AND ADDRESS_1 = #{address_1}



		ORDER BY 단골수 DESC NULLS LAST)
		JOIN AD AD USING(SHOP_NO)
		WHERE ROWNUM <= 9
		AND VIEW_CNT !=0
		AND BSTATUS != 'N'

SELECT N_NO , NEWS_TITLE , SHOP_NEWS , SHOP_NO , NN.T_NO , ADDRESS_3 , NN.FILE_PATH , ORIGIN_NAME , CHANGE_NAME , STATUS , F_NO FROM ( SELECT N.N_NO , NEWS_TITLE , SHOP_NEWS , SHOP_NO , N.T_NO , ADDRESS_3 , FILE_PATH , ORIGIN_NAME , CHANGE_NAME , STATUS , NF.F_NO FROM NEWS N JOIN TOWN T ON(T.T_NO = N.T_NO) LEFT JOIN NEWS_FILE NF ON(N.N_NO = NF.N_NO) LEFT JOIN ADDFILE A ON(A.F_NO = NF.F_NO)
AND ADDRESS_3 = #{address_3} AND ADDRESS_2 = #{address_2} AND ADDRESS_1 = #{address_1}
AND ADDRESS_2 = #{address_2} AND ADDRESS_1 = #{address_1}
AND ADDRESS_1 = #{address_1}
ORDER BY N_NO DESC ) NN JOIN AD AD USING(SHOP_NO) JOIN BUS_INFO USING(SHOP_NO) WHERE ROWNUM <= 4 AND VIEW_CNT !=0 AND BSTATUS != 'N'

		SELECT *
		FROM
		(SELECT V.*
        FROM V_BUS_INFO V
        JOIN TOWN T ON(T.T_NO = V.T_NO)



				AND ADDRESS_3 = #{address_3}
				AND ADDRESS_2 = #{address_2}
				AND ADDRESS_1 = #{address_1}


				AND ADDRESS_2 = #{address_2}
				AND ADDRESS_1 = #{address_1}


				AND ADDRESS_1 = #{address_1}



		ORDER BY 평균별점 DESC NULLS LAST)
		JOIN AD AD USING(SHOP_NO)
		WHERE ROWNUM <= 9
		AND VIEW_CNT !=0
		AND BSTATUS != 'N'

		SELECT *
		FROM
		(SELECT V.*
        FROM V_BUS_INFO V
        JOIN TOWN T ON(T.T_NO = V.T_NO)



				AND ADDRESS_3 = #{address_3}
				AND ADDRESS_2 = #{address_2}
				AND ADDRESS_1 = #{address_1}


				AND ADDRESS_2 = #{address_2}
				AND ADDRESS_1 = #{address_1}


				AND ADDRESS_1 = #{address_1}



		ORDER BY 후기수 DESC NULLS LAST)
		JOIN AD AD USING(SHOP_NO)
		WHERE ROWNUM <= 9
		AND VIEW_CNT !=0
		AND BSTATUS != 'N'

SELECT N.N_NO , NEWS_TITLE , SHOP_NEWS , N.SHOP_NO , N.T_NO , ADDRESS_3 , FILE_PATH , ORIGIN_NAME , CHANGE_NAME , A.STATUS , NF.F_NO FROM NEWS N JOIN TOWN T ON(T.T_NO = N.T_NO) LEFT JOIN NEWS_FILE NF ON(N.N_NO = NF.N_NO) LEFT JOIN ADDFILE A ON(A.F_NO = NF.F_NO) JOIN BUS_INFO B ON(B.SHOP_NO = N.SHOP_NO) JOIN AD AD ON(AD.SHOP_NO = N.SHOP_NO)
AND ADDRESS_3 = #{address_3} AND ADDRESS_2 = #{address_2} AND ADDRESS_1 = #{address_1}
AND ADDRESS_2 = #{address_2} AND ADDRESS_1 = #{address_1}
AND ADDRESS_1 = #{address_1}
AND SHOP_CATE='일자리' AND VIEW_CNT !=0 AND BSTATUS != 'N' ORDER BY N_NO DESC

