mapper.insert:
INSERT
INTO BUS_INFO
(
	SHOP_NO
  , SHOP_CATE
  , SHOP_ADD
  , SHOP_INTRO
  , SHOP_TIME1
  , TOP_MENU
  , MENU_INFO
  , SHOP_PHONE
  , USER_ID
  , AD_CASH
  , SHOP_TIME2
  , SHOP_TIME3
  , T_NO
  , SHOP_NAME

)
VALUES
(
	SEQ_SHOPNO.NEXTVAL
  , #{shopCate}
  , #{shopAdd}
  , #{shopIntro}
  , #{shopTime1}
  , #{topMenu}
  , #{menuInfo}
  , #{shopPhone}
  , #{userId}
  , DEFAULT
  , #{shopTime2}
  , #{shopTime3}
  , #{tNo}
  , #{shopName}
)

INSERT
INTO NEWS
(
	N_NO
  , NEWS_TITLE
  , SHOP_NEWS
  , SHOP_NO
  , T_NO


)
VALUES
(
	SEQ_NNO.NEXTVAL
  , #{newsTitle}
  , #{shopNews}
  , #{shopNo}
  , #{tNo}

)

mapper.update:
UPDATE
	BUS_INFO
SET
 	SHOP_CATE = #{shopCate}
  , SHOP_ADD  = #{shopAdd}
  , SHOP_INTRO = #{shopIntro}
  , SHOP_TIME1 = #{shopTime1}
  , TOP_MENU   = #{topMenu}	
  , MENU_INFO  = #{menuInfo}
  , SHOP_PHONE = #{shopPhone}
  , SHOP_TIME2 = #{shopTime2}
  , SHOP_TIME3 = #{shopTime3}
  , SHOP_NAME  = #{shopName}
WHERE
	SHOP_NO	 = #{shopNo}

mapper.insert:

		INSERT INTO ADDFILE(
			F_NO
		  , ORIGIN_NAME
		  , CHANGE_NAME
          , STATUS
          , FILE_LEVEL
		)
		VALUES
		(
			F_SEQ.NEXTVAL
		  , #{originName}
		  , #{changeName}
          , DEFAULT
          , #{file_level}

		)

INSERT INTO FAVORITE
( 	SHOP_NO
  , USER_ID
  , F_DATE

)
VALUES
(	#{shopNo}
  , #{userId}
  , DEFAULT

)
