mapper.update:
UPDATE
     USER_INFO
  SET
      POINT = #{point}
    , ADMIN_MEMO = #{admin_memo}
 WHERE
      USER_ID = #{user_id}

UPDATE
		USER_INFO
   SET
   		PHOTO = #{photo}
 WHERE
 		USER_ID = #{user_id}

UPDATE
		USER_INFO
   SET
   		POINT = POINT-#{user_point}
 WHERE
 		USER_ID = #{user_id}

mapper.select:
SELECT COUNT(*)
  FROM FOLLOW
 WHERE FOLLOWER =#{user_id}
   AND FOLLOWING =#{seller}

mapper.delete:
		DELETE FROM FOLLOW 
	    WHERE FOLLOWER = #{user_id}
   		  AND FOLLOWING = #{seller}

mapper.select:

	SELECT COUNT(FOLLOWING) CNT
	  FROM FOLLOW
     WHERE FOLLOWER = #{user_id}


	SELECT COUNT(FOLLOWER) CNT
	  FROM FOLLOW
     WHERE FOLLOWING = #{user_id}

