mapper.insert:
	INSERT
		INTO QNA
		(
		   QA_NO
	     , QA_TITLE
	     , QA_CONTENT
	     , QA_DATE
	     , USER_ID
	     , FAQ
	     
		)
		VALUES
		(
			SEQ_QA_NO.NEXTVAL
		  , #{qa_title}
		  , #{qa_content}
		  , SYSDATE
		  , #{user_id}
		  , #{faq}
		)

mapper.update:
UPDATE
       QNA
   SET
       QA_TITLE = #{qa_title}
     , QA_CONTENT = #{qa_content}
     , QA_MODIFY = SYSDATE
 WHERE
       QA_NO = #{qa_no}
